import { ADD_FAV, REMOVE_FAV, FILTER, ORDER } from './actions-types';

const initialState = {
    myFav: [],
    allCharacters: []
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_FAV:
            return {...state,
                myFav: [...state.allCharacters, action.payload],
                allCharacters: [...state.allCharacters, action.payload]
            }
        case REMOVE_FAV:
            return {...state,
                myFav: state.myFav.filter(fav => fav.id !== action.payload)
                }
        case FILTER:
            const allCharactersFiltered = state.allCharacters.filter(character => character.gender === action.payload)
            return {
                ...state,
                myFav: allCharactersFiltered
            }
        case ORDER:
            return {
                ...state,
                
            }
        default:
            return {...state}
    }

}

export default reducer;
