const validate = (userData) => {
    const errors = {}

    if (!(/\S+@\S+\.\S+/.test(userData.email) && userData.email.length >= 35)) {
        errors.mail = 'El email no es válido.'
    } else {
        errors.mail = ''
    }

    if (!(/\d/.test(userData.password) && userData.password.length > 5 && userData.password.length <= 10)) {
        errors.password = 'La contraseña debe tener al menos un número y tener una longitud entre 6 y 10 caracteres.'
    } else {
        errors.password = ''
    }
}

export default validate;
