import style from './Form.module.css';
import { validate } from './validation.js';
import { useState } from 'react';

const Form = () => {
    const [userData, setData] = useState({
        email: '',
        password: ''
    })

    const [errors, setErrors] = useState({
        email: '',
        password: ''
    })

    const validate = () => {
        if (!(/\S+@\S+\.\S+/.test(userData.email) && userData.email.length > 0)) {
            setErrors({
                ...errors,
                email: 'El mail no es válido.'
            })
        } else if (!(/\S+@\S+\.\S+/.test(userData.email) && userData.email.length < 35)) {
            setErrors({
                ...errors,
                email: 'El mail tiene que tener menos de 35 caracteres.'
            })
        } else {
            setErrors({
                ...errors,
                email: ''
            })
        }
    }
    

    const handleChange = (event) => {
        setData({
            ...userData,
            [event.target.name]: event.target.value
        })
        validate()
    }



    return(
        <form>
            <label htmlFor='email'>Email:</label>
            <input type='email' name='email' placeholder='Ingresa tu mail' value={userData.email} onChange={handleChange}></input>
            { errors.email && <p>{errors.email}</p> }

            <label htmlFor='password'>Contraseña:</label>
            <input type='password' name='password' placeholder='Ingresa tu contraseña' value={userData.password} onChange={handleChange}></input>'

            <button type='submit'>Enviar</button>
        </form>
    )
};

export default Form;
